#break between my section#before you send, check with main file for arguments# hiimport sqlite3import tkinter as tkfrom tkinter import ttkfrom tkinter import messageboxdef AddTrayFunct(TofPEntry, DateEntry):    # create connection    TofPEntry = TofPEntry.get()    try:        TofPEntry = int(TofPEntry)    except ValueError:        tk.messagebox.showerror("ERROR", "The inputted Herb ID is not an integer.")    DateEntry = DateEntry.get()    # specify the date should be YYYY-MM-DD    # TrayTofEntryVar.get()    # TrayDateEntryVar.get()    conn = sqlite3.connect('Urban Greens5.db')    cur = conn.cursor()    cur.execute('''SELECT EXISTS(SELECT 1 FROM Herbs WHERE HerbID = ?)''', (TofPEntry,))    record = cur.fetchone()    if record[0] == 1:        cur.execute('''INSERT INTO Tray(HerbID, PlantDate)                    VALUES(?,?)''', (TofPEntry, DateEntry,))    else:        tk.messagebox.showinfo("ERROR", "The inputted Herb ID does not exist.")    conn.commit()    conn.close()def AddHerbFunct(HerbTypeEntry):    HerbTypeEntry = HerbTypeEntry.get()    #create connection    conn = sqlite3.connect('Urban Greens5.db')    cur = conn.cursor()    cur.execute('''INSERT INTO Herbs (Herb) VALUES(?)''', (HerbTypeEntry,))    conn.commit()    conn.close()def AddHarvestFunct(TrayIDEntry, AmountEntry, DateEntry):    # create connection    TrayIDEntry = TrayIDEntry.get()    AmountEntry = AmountEntry.get()    DateEntry = DateEntry.get()    TrayIDEntry = int(TrayIDEntry)    conn = sqlite3.connect('Urban Greens5.db')    cur = conn.cursor()    cur.execute('''SELECT EXISTS(SELECT 1 FROM Tray WHERE TrayID = ?)''', (TrayIDEntry,))    record = cur.fetchone()    if record[0] == 1:        cur.execute('''INSERT INTO Harvest(TrayID,HarvestDate,HarvestAmount)         VALUES(?,?,?)''', (TrayIDEntry, AmountEntry, DateEntry))    else:        tk.messagebox.showinfo("ERROR", "The inputted Tray ID does not exist.")    conn.commit()    conn.close()def AddOrderFunct(OrderIDEntry, VendorIDEntry, HerbIDEntry,AmountEntry, DateEntry):   #create connection    OrderIDEntry = OrderIDEntry.get()    VendorIDEntry = VendorIDEntry.get()    HerbIDEntry = HerbIDEntry.get()    AmountEntry = AmountEntry.get()    DateEntry = DateEntry.get()    OrderIDEntry = int(OrderIDEntry)    VendorIDEntry = int(VendorIDEntry)    HerbIDEntry = int(HerbIDEntry)    conn = sqlite3.connect('Urban Greens5.db')    cur = conn.cursor()    cur.execute('''SELECT EXISTS(SELECT 1 FROM Vendors WHERE VendorID = ?)''', (VendorIDEntry,))    record = cur.fetchone()    cur.execute('''SELECT EXISTS(SELECT 1 FROM Herbs WHERE HerbID = ?)''', (HerbIDEntry,))    record2 = cur.fetchone()    if record[0] == 1:        if record2[0] == 1:            cur.execute('''INSERT INTO Orders(OrderID, VendorID, HerbID, OrderAmount, OrderDate)                VALUES(?,?,?,?,?)''', (OrderIDEntry,VendorIDEntry,HerbIDEntry,AmountEntry,DateEntry))        else:            tk.messagebox.showinfo("ERROR", "The inputted Herb ID does not exist.")    else:        tk.messagebox.showinfo("ERROR", "The inputted Vendor ID does not exist.")    conn.commit()    conn.close()def AddVendorsFunct(VendorNameEntry, VendorStreetEntry,                    VendorCityEntry, VendorStateEntry,                    VendorZipEntry):    #create connection    VendorNameEntry = VendorNameEntry.get()    VendorStreetEntry = VendorStreetEntry.get()    VendorCityEntry = VendorCityEntry.get()    VendorStateEntry = VendorStateEntry.get()    VendorZipEntry = VendorZipEntry.get()    conn = sqlite3.connect('Urban Greens5.db')    cur = conn.cursor()    cur.execute('''INSERT INTO Vendors(VendorName, Street, City, State, Zipcode)            VALUES(?,?,?,?,?)''', (VendorNameEntry, VendorStreetEntry, VendorCityEntry, VendorStateEntry, VendorZipEntry))    conn.commit()    conn.close()